mapper.select:
SELECT role.id, role.project_id, pro.project_name as "projectName", role.role_name, role.role_code, role.role_type, role.remark, role.status, role.create_time, role.num FROM sys_role role LEFT JOIN sys_project pro on role.project_id = pro.id where role.del = false and pro.del = false and role.role_type != 'superadmin'
and role.project_id = #{params.projectId}
and role.role_name like '%${params.roleName}%'
and role.role_type = #{params.roleType}
and role.create_time >= #{params.startTime}
and role.create_time <= #{params.endTime}
and role.id in (SELECT role_id FROM sys_role_app where app_id = #{params.appId})
order by role.num asc, role.create_time desc

SELECT a.id, a.role_id, b.role_name AS "roleName", a.app_id, c.app_name AS "appName", a.resource_id, d.menu_name as "menuName", d.level as "level", d.parent_id as "parentId", c.app_type as "appType" FROM sys_role_resource a LEFT JOIN sys_role b ON a.role_id = b.id LEFT JOIN sys_application c ON a.app_id = c.id LEFT JOIN sys_resource d ON a.resource_id = d.id WHERE b.del = FALSE AND c.del = FALSE
and a.project_id = #{params.projectId}
and a.app_id = #{params.appId}
and a.role_id = #{params.roleId}

SELECT a.id, a.role_id, b.role_name AS "roleName", a.app_id, c.app_name AS "appName", c.app_type as "appType" FROM sys_role_app a LEFT JOIN sys_role b ON a.role_id = b.id LEFT JOIN sys_application c ON a.app_id = c.id WHERE b.del = FALSE AND c.del = FALSE
and a.project_id = #{params.projectId}
and a.app_id = #{params.appId}
and a.role_id = #{params.roleId}
and c.app_code = #{params.appCode}
